(8279, 'Goomy Clicker', 'goomy-clicker', 'HTML5', 0, 5, 14, 411, '0', 'http://games.crazygames.com/en_US/goomy-clicker/index.html', 'https://crazygames.com', 'en', '', 20, 0, 0, 0, 0, 'Goomy Clicker is a Cookie Clicker inspired incremental game made by Joe Zeng.', '', 'goomy-clickerr.png', 1, 1, '2022-08-08 16:30:46', '2022-08-08 16:30:46', NULL),
(8280, 'Bridge of Doom', 'bridge-of-doom', 'HTML5', 0, 4, 13, 243, '0', 'https://games.crazygames.com/en_US/bridge-of-doom/index.html', 'https://crazygames.com', 'en', 'Robert (BlackMoon)', 20, 0, 0, 0, 0, 'Bridge of Doom is a fun platform game with a unique concept. You must control your fierce and braze dwarf and help him pass over the deadly bridge of doom! You will encounter many enemies and obstacles on your way.', 'Press left mouse button to move/attack.', 'bridge-of-doomm.png', 1, 1, '2022-08-08 16:30:47', '2022-08-08 16:30:47', NULL),
(8281, 'Progress Quest', 'progress-quest', 'HTML5', 0, 3, 19, 864, '0', 'http://games.crazygames.com/en_US/progress-quest/index.html', 'https://crazygames.com', 'en', '', 20, 0, 0, 0, 0, 'Progress Quest is the oldest idle game in existence. It was developed in 2002 as a parody of EverQuest. The developer labeled it \'the first 0-player game\', because as a player there\'s nothing you can do!', '', 'progress-questt.png', 1, 1, '2022-08-08 16:30:47', '2022-08-08 16:30:47', NULL),
(8282, 'The Wanderer', 'the-wanderer', 'HTML5', 0, 3, 12, 1059, '0', 'https://games.crazygames.com/en_US/the-wanderer/index.html', 'https://crazygames.com', 'en', 'Jamie Parish.', 20, 0, 0, 0, 0, 'The Wanderer is a simple but engaging text based game in which you must help a lone wanderer survive in the midst of an apocalypse. A nuclear war has broken out and the world’s population and cities have been devastated in the process. You must now explore this strange new landscape and complete random events each day to survive.\n', '', 'the-wandererr.png', 1, 1, '2022-08-08 16:30:48', '2022-08-08 16:30:48', NULL),
(8283, 'Donut Factory', 'donut-factory', 'HTML5', 0, 5, 13, 754, '0', 'https://games.crazygames.com/en_US/donut-factory/index.html', 'https://crazygames.com', 'en', '', 20, 0, 0, 0, 0, 'Donut Factory is a superb cooking game in which you get to make delicious donuts! You must find different ingredients to use and upgrade your donuts so that you can impress your customers. You must try and become the world’s greatest donut creator!\n', '', 'donut-factoryy.png', 1, 1, '2022-08-08 16:30:48', '2022-08-08 16:30:48', NULL),
(8284, 'Idle Fight Combat Tycoon', 'idle-fight-combat-tycoon', 'HTML5', 0, 5, 12, 84, '0', 'https://games.crazygames.com/en_US/idle-fight-combat-tycoon/index.html', 'https://crazygames.com', 'en', 'megga', 20, 0, 0, 0, 0, 'Idle Fight Combat Tycoon is an idle game to fight against monsters where you can also learn new fighting techniques, buy and equip a weapon, and also buy a hat that has its own perk and also makes you look cooler! Click on the coin icon to get some money. Have fun!', 'Use the left button to play the game.', 'idle-fight-combat-tycoon1.png', 1, 1, '2022-08-08 16:30:49', '2022-08-08 16:30:49', NULL),
(8285, 'Mining in Notebook', 'mining-in-notebook', 'HTML5', 0, 3, 17, 1055, '0', 'https://games.crazygames.com/en_US/mining-in-notebook/index.html', 'https://crazygames.com', 'en', '', 20, 0, 0, 0, 0, 'Mining in Notebook is an idle clicker game with notebook-style graphics! There are many different tools and resources that can make you engage more in the game. Can you unlock everything?\n', '', 'mining-in-notebook.jpg', 1, 1, '2022-08-08 16:30:49', '2022-08-08 16:30:49', NULL),
(8286, 'Glass Factory', 'glass-factory', 'HTML5', 0, 5, 13, 373, '0', 'https://games.crazygames.com/en_US/glass-factory/index.html', 'https://crazygames.com', 'en', 'LeimurGames.', 20, 0, 0, 0, 0, 'Glass Factory is a clicker game in which you maintain a glass factory. Buy new equipment for the plant and equipment for sand extraction. Will you become a millionaire? It depends on you.\n', '', 'glass-factory.jpg', 1, 1, '2022-08-08 16:30:50', '2022-08-08 16:30:50', NULL),
(8287, 'Juice Production Tycoon', 'juice-production-tycoon', 'HTML5', 0, 4, 18, 816, '0', 'https://games.crazygames.com/en_US/juice-production-tycoon/index.html', 'https://crazygames.com', 'en', 'clicking, chopping, and juicing fruit', 20, 0, 0, 0, 0, 'Juice Production Tycoon is an incremental clicker game where you chop fruits to earn money. You can upgrade your production line for more exotic fruits and better equipment with the money you earn. Become the best juice maker in town in Juice Production Tycoon!', 'Mouse and touch', 'juice-production-tycoon.jpg', 1, 1, '2022-08-08 16:30:50', '2022-08-08 16:30:50', NULL),
(8288, 'Farming Season Idle', 'farming-season-idle', 'HTML5', 0, 5, 14, 211, '0', 'https://games.crazygames.com/en_US/farming-season-idle/index.html', 'https://crazygames.com', 'en', '', 20, 0, 0, 0, 0, 'Farming Season Idle is a 2D idle farming game. Plant and harvest crops, upgrade your equipment, and expand your fields to earn even more money to thrive and prosper on your lands.', 'Use the left mouse button to play the game.', 'farming-season-idle.jpg', 1, 1, '2022-08-08 16:30:51', '2022-08-08 16:30:51', NULL);
