(8450, 'Shadow Dungeon', 'shadow-dungeon', 'HTML5', 0, 5, 20, 946, '0', 'https://games.crazygames.com/en_US/shadow-dungeon/index.html', 'https://crazygames.com', 'en', 'Benjamin Man.', 22, 0, 0, 0, 0, 'Shadow Dungeon is a tricky hard game with unexpected surprises.\n', '\nWASD to move\nClick to shoot\nHold for machine gun\n', 'shadow-dungeon.jpg', 1, 1, '2022-08-08 16:32:32', '2022-08-08 16:32:32', NULL),
(8451, 'Kogama: Escape from Prison', 'kogama-escape-from-prison', 'HTML5', 0, 5, 14, 1107, '0', 'https://games.crazygames.com/en_US/kogama-escape-from-prison/index.html', 'https://crazygames.com', 'en', 'Kogama', 22, 0, 0, 0, 0, 'Can you overcome all the hardships and difficulties to escape from prison?', '\nWASD or arrow keys to move\nSpace bar to jump\nO to enter fullscreen\n', 'kogama-escape-from-prisonn.png', 1, 1, '2022-08-08 16:32:32', '2022-08-08 16:32:32', NULL),
(8452, 'Terraria (Scratch Version)', 'terraria-scratch-version', 'HTML5', 0, 4, 13, 1061, '0', 'https://games.crazygames.com/en_US/terraria/index.html', 'https://crazygames.com', 'en', '', 22, 0, 0, 0, 0, 'Terraria is an adventure game, a bit more like Minecraft that allows you to dig, fight, explore, and build!\n', '\nWASD = move\nLeft-click = dig / place\n0-9 = select tools\nE = open / close inventory\nK = creative Mode\nR = restart\n', 'terraria-scratch-version3.png', 1, 1, '2022-08-08 16:32:33', '2022-08-08 16:32:33', NULL),
(8453, 'FingerBall', 'fingerball', 'HTML5', 0, 4, 19, 333, '0', 'https://games.crazygames.com/en_US/fingerball/index.html', 'https://crazygames.com', 'en', 'Youdevice!.', 22, 0, 0, 0, 0, 'FingerBall is a challenging game in which you need to control the smiley ball with your finger to complete the objectives.\n', '', 'fingerball5.png', 1, 1, '2022-08-08 16:32:33', '2022-08-08 16:32:33', NULL),
(8454, 'Zoo\'s Mad', 'zoo-s-mad', 'HTML5', 0, 5, 19, 1034, '0', 'https://games.crazygames.com/en_US/zoo-s-mad/index.html', 'https://crazygames.com', 'en', 'MicoWata.', 22, 0, 0, 0, 0, 'The land of Zoo got cursed by a terrifying monster. Now, the land is mad. You are a magician and you have many magical cards to help your people. Use them in Zoo\'s Mad, a unique adventure game!\n', '', 'zoo-s-mad.jpg', 1, 1, '2022-08-08 16:32:34', '2022-08-08 16:32:34', NULL),
(8455, 'Missed Messages', 'missed-messages', 'HTML5', 0, 4, 17, 1052, '0', 'https://games.crazygames.com/en_US/missed-messages/index.html', 'https://crazygames.com', 'en', 'Angela He (Zephyo), the same developer who made another visual novel called You Left Me. You can fol', 22, 0, 0, 0, 0, 'WARNING: This game is suited only for players with age of 17 and above (players discretion is strongly advised). Missed Messages is a beautifully designed visual novel about life struggles and how we want to go through them.\n', '', 'missed-messages1.png', 1, 1, '2022-08-08 16:32:34', '2022-08-08 16:32:34', NULL),
(8456, 'Minigod', 'minigod', 'HTML5', 0, 5, 14, 604, '0', 'https://games.crazygames.com/en_US/minigod/index.html', 'https://crazygames.com', 'en', 'Jack Le Hamster.', 22, 0, 0, 0, 0, 'Become a mini god and grow your world using some automation and complex system!\n', '\nArrow keys to move\nSpace bar to dig\nTab to exit/enter spaceship\n', 'minigod6.png', 1, 1, '2022-08-08 16:32:35', '2022-08-08 16:32:35', NULL),
(8457, 'Waitventure', 'waitventure', 'HTML5', 0, 5, 16, 393, '0', 'https://games.crazygames.com/en_US/waitventure/index.html', 'https://crazygames.com', 'en', 'Waitventure was', 22, 0, 0, 0, 0, 'Waitventure is an idle game to recruit monsters and fusion them to get stronger allies in your party. Train your player, level up, increase their stats, and learn new skills, by using your time currency. When you are ready for the adventure, enter the dungeon where your party will fight enemies in automated battles. Sometimes the enemies will join your team! If your allies are strong enough, you can fuse them to get monsters and humans from a higher tier, with different stats and abilities. The dungeon is divided into years, months and days, like a calendar. At the end of each day, there is a boss, that is stronger than regular enemies, but gives more EXP. Can you reach 31st December and beat the final boss? ', '\n1-5 = Navigate through the town menus\n6 = Level up all Party members\n7 = Heal all Party members\n8 = Enter the Dungeon\n9 = Repeat the last Dungeon Level\n0 = Reset Dungeon to Max Level\n', 'waitventure2.png', 1, 1, '2022-08-08 16:32:36', '2022-08-08 16:32:36', NULL);
