(8659, 'Get Ready With Me: Christmas Edition', 'get-ready-with-me-christmas-edition', 'HTML5', 0, 4, 19, 1182, '0', 'https://games.crazygames.com/en_US/get-ready-with-me-christmas-edition/index.html', 'https://crazygames.com', 'en', '', 26, 0, 0, 0, 0, 'Get Ready With Me: Christmas Edition is a beauty game with a Christmas theme. Help the girl for the holiday by doing makeup, making cookies, and helping with her outfit too. Get ready to hang out with her friends.', 'Use the left mouse button to interact with the objects.', 'get-ready-with-me-christmas-edition.jpg', 1, 1, '2022-08-08 16:34:31', '2022-08-08 16:34:31', NULL),
(8660, 'Moana Holiday', 'moana-holiday', 'HTML5', 0, 5, 15, 751, '0', 'https://games.crazygames.com/en_US/moana-holiday/index.html', 'https://crazygames.com', 'en', 'IDEA Studios from Romania.', 26, 0, 0, 0, 0, 'Now that Moana has successfully saved her Island she wants to take a break and relax! She decides to travel around the world to some far-flung locations such as Paris, Sri Lanka and the US. Before Moana can travel she wants to find the right outfit for each location she is visiting – it is your job to help her pick her clothes and prepare for her adventures!\n', '', 'moana-holidayy.png', 1, 1, '2022-08-08 16:34:31', '2022-08-08 16:34:31', NULL),
(8661, 'Year Round Fashionista: Anna', 'year-round-fashionista-anna', 'HTML5', 0, 3, 16, 965, '0', 'https://games.crazygames.com/en_US/year-round-fashionista-anna/index.html', 'https://crazygames.com', 'en', 'Idea Studios.', 26, 0, 0, 0, 0, 'Year Round Fashionista: Anna is another gorgeous dress-up game that lets you kit out the Disney princess Anna from the popular film Frozen! This game has similar gameplay and features to Year Round Fashionista: Elsa but it is now her sister Anna’s turn for a makeover! During each month of the year you must help Anna choose a beautiful outfit and ensure that her fashion matches the season.\n', '', 'year-round-fashionista-annaa.png', 1, 1, '2022-08-08 16:34:31', '2022-08-08 16:34:31', NULL),
(8662, 'Funny Tattoo Shop', 'funny-tattoo-shop', 'HTML5', 0, 4, 13, 741, '0', 'https://games.crazygames.com/en_US/funny-tattoo-shop/index.html', 'https://crazygames.com', 'en', 'Go Panda Games.', 26, 0, 0, 0, 0, 'Choose what\'s your favorite objects and make tattoos on your arm or leg. Feel free to modify and color them as you wish!\n', '', 'funny-tattoo-shop.jpg', 1, 1, '2022-08-08 16:34:32', '2022-08-08 16:34:32', NULL),
(8663, 'Barbie Date Crashing', 'barbie-date-crashing', 'HTML5', 0, 5, 13, 1044, '0', 'http://games.crazygames.com/en_US/barbie-date-crashing/index.html', 'https://crazygames.com', 'en', 'Barbie Date Crashing is a dress up game', 26, 0, 0, 0, 0, 'Ken broke up with Barbie! And now he is dating another girl. Barbie plans to crash their date with her new charming outfit. In this game you have to dress her up; choose a gorgeous hairstyle and decide whether she is going to wear a top with a skirt or a dress, and don\'t forget the accessories. No matter what she wears, she will look beautiful! Now, who is the gorgeous girl that Ken left? Have fun!', 'Left click to choose outfits.', 'barbie-date-crashingg.png', 1, 1, '2022-08-08 16:34:32', '2022-08-08 16:34:32', NULL),
(8664, 'Belle City Girl', 'belle-city-girl', 'HTML5', 0, 4, 18, 237, '0', 'https://games.crazygames.com/en_US/belle-city-girl/index.html', 'https://crazygames.com', 'en', '', 26, 0, 0, 0, 0, 'Belle City Girl is a fun dress up game. You take control of Belle who is a beautiful young girl that is bored with her ordinary life. To change this, she moves to New York City to start a new life and become successful and popular!\n', '', 'belle-city-girll.png', 1, 1, '2022-08-08 16:34:33', '2022-08-08 16:34:33', NULL),
(8665, 'Cooking Korean Lesson', 'cooking-korean-lesson', 'HTML5', 0, 4, 11, 87, '0', 'https://games.crazygames.com/en_US/cooking-korean-lesson/index.html', 'https://crazygames.com', 'en', 'Go Panda Games.', 26, 0, 0, 0, 0, 'Do you know how to make kimchi? Do you know how to make bibimbap? Let\'s play together and serve the best kimchi and bibimbap you can make!\n', '', 'cooking-korean-lesson.jpg', 1, 1, '2022-08-08 16:34:33', '2022-08-08 16:34:33', NULL),
