(8839, 'Pool 8', 'pool-8', 'HTML5', 0, 4, 15, 1056, '0', 'https://games.crazygames.com/en_US/pool-8/index.html', 'https://crazygames.com', 'en', 'Appventure', 28, 0, 0, 0, 0, 'Pool 8 is a brain-teasing puzzle game based on a Pool board. Pot the balls in the correct order to move through a series of increasingly difficult challenges!', 'Swipe or arrow keys', 'pool-8.jpg', 1, 1, '2022-08-08 16:36:15', '2022-08-08 16:36:15', NULL),
(8840, 'Mahjong Relax', 'mahjong-relax', 'HTML5', 0, 3, 13, 1015, '0', 'https://games.crazygames.com/en_US/mahjong-relax/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Mahjong Relax is a calming Mahjong game to play with unlimited undo and without a time limit. If you want more challenge, try the timed version in Mahjong Time.\n', '', 'mahjong-relax8.png', 1, 1, '2022-08-08 16:36:15', '2022-08-08 16:36:15', NULL),
(8841, 'Big Giant Games (Prison Escape Puzzle)', 'big-giant-games-prison-escape-puzzle', 'HTML5', 0, 4, 15, 729, '0', 'https://games.crazygames.com/en_US/big-giant-games/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Big Giant Games is a collection of thematic room escape games. Get some clues in the area and find your way to escape.\n', '', 'big-giant-games-prison-escape-puzzle2.png', 1, 1, '2022-08-08 16:36:16', '2022-08-08 16:36:16', NULL),
(8842, 'Popit vs Spinner', 'popit-vs-spinner', 'HTML5', 0, 5, 13, 136, '0', 'https://games.crazygames.com/en_US/popit-vs-spinner/index.html', 'https://crazygames.com', 'en', 'adgard.', 28, 0, 0, 0, 0, 'Pop It vs Spinner is a collapse puzzle game where the objective is to remove all the bubbles by clicking at least 2 of the same color bubbles at once. You can use the spinner when there are no moves available.', 'Use the left mouse button to pop the bubbles.', 'popit-vs-spinner.jpg', 1, 1, '2022-08-08 16:36:16', '2022-08-08 16:36:16', NULL),
(8843, 'Pudding Monsters', 'pudding-monsters', 'HTML5', 0, 5, 13, 76, '0', 'https://games.crazygames.com/en_US/pudding-monsters/index.html', 'https://crazygames.com', 'en', 'ZeptoLab. You can play the online version for free here on CrazyGames.com', 28, 0, 0, 0, 0, 'Pudding Monsters is a casual but fun game developed by ZeptoLab in which you must attempt to place all the different pudding monsters together to form one giant pudding monsters!\n', '', 'pudding-monsterss.png', 1, 1, '2022-08-08 16:36:17', '2022-08-08 16:36:17', NULL),
(8844, 'Orakyubu', 'orakyubu', 'HTML5', 0, 4, 14, 601, '0', 'https://games.crazygames.com/en_US/orakyubu/index.html', 'https://crazygames.com', 'en', 'Orakyubu is', 28, 0, 0, 0, 0, 'Orakyubu is a 2D Sokoban-style puzzle game in a 3D environment where you need to push the boxes into the targets.', 'GNU General Public License v2.0', 'orakyubu8.png', 1, 1, '2022-08-08 16:36:18', '2022-08-08 16:36:18', NULL),
(8846, 'Flip Palace', 'flip-palace', 'HTML5', 0, 3, 20, 819, '0', 'https://games.crazygames.com/en_US/flip-palace/index.html', 'https://crazygames.com', 'en', 'Levi', 28, 0, 0, 0, 0, 'Flip Palace is a puzzle-platformer game where you can flip into walls to find a way to reach the exit portals.', '\nAD or left and right arrow keys = move\nWS or up and down arrow keys = flip / jump\nSpace = jump\nEsc = pause\n', 'flip-palace.jpg', 1, 1, '2022-08-08 16:36:19', '2022-08-08 16:36:19', NULL),
(8847, 'Hexa Dungeon', 'hexa-dungeon', 'HTML5', 0, 4, 19, 487, '0', 'https://games.crazygames.com/en_US/hexa-dungeon/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Hexa Dungeon is a match-3 game to connect as many tiles of the same color to attack the enemy.', 'Drag the left mouse button to connect tiles, release to attack the enemy.', 'hexa-dungeon0.png', 1, 1, '2022-08-08 16:36:20', '2022-08-08 16:36:20', NULL),
